import { Box,TextField,Button,Typography,styled } from '@mui/material';
import { useState } from 'react';

const Component =styled(Box)`
   width:400px;
   margin:auto;
   box-shadow:5px 2px 5px 2px; 
   `
   const Image=styled('img')({
    width:100,//by default it takes px 
    margin:'auto',
    display:'flex',
    padding:'50px 0 0 '
   });
   const Wrapper = styled(Box)`
   padding: 25px 35px;
   display: flex;
   flex: 1;
   overflow: auto;
   flex-direction: column;
   & > div, & > button, & > p {
       margin-top: 20px;
   }
`;
const LoginButton = styled(Button)`
    text-transform: none;
    background: #FB641B;
    color: #fff;
    height: 48px;
    border-radius: 2px;
`;


const SignupButton = styled(Button)`
    text-transform: none;
    background: #fff;
    color: #2874f0;
    height: 48px;
    border-radius: 2px;
    box-shadow: 0 2px 4px 0 rgb(0 0 0 / 20%);
`;

const Text = styled(Typography)`
    color: #878787;
    font-size: 12px;
`;

const Login = () => {

    const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

    const [account,setaccount]=useState('login');

    const handeltogel=()=>{
        account==='signup' ? setaccount('login') : setaccount('signup');
    }  

    const oninputchange=(e)=>{

    }
    return (
        <Component>
            <Box>
            <img src={imageURL} alt="login" />
            {
                account==='login' ?
            <Wrapper>
            <TextField variant='standard' label='Enter user name'/>
            <TextField variant='standard'label='Enter Password'/>
            <LoginButton variant='contained'>Log in</LoginButton>
            <Text style={{ textAlign: 'center' }}>OR</Text>
            <SignupButton onClick={handeltogel} >Create account</SignupButton>
            </Wrapper>
            :
            <Wrapper>
            <TextField variant='standard' onChange={(e)=>oninputchange(e)} label='Name'/>
            <TextField variant='standard' onChange={(e)=>oninputchange(e)} label='Enter user name'/>
            <TextField variant='standard'onChange={(e)=>oninputchange(e)} label='Enter Password'/>

            <SignupButton>Sign up</SignupButton>
            <Text style={{ textAlign: 'center' }}>OR</Text>
            <LoginButton variant='contained' onClick={handeltogel}>Already have account </LoginButton>
            
            
            </Wrapper>
      }
            </Box>
        </Component>
          );
}
 
export default Login;
